import React from "react";

class TodoItem extends React.Component {

  handleDelete() {
    this.props.delete(this.props.index);
  }

  render() {
    return (
      <div onClick={this.handleDelete.bind(this)}>
        {/*子组件通过props接受父组件传递过来的参数 */}
        {this.props.content}
      </div>
    )
  }
}

export default TodoItem;
